// src/App.js
import React, { useRef } from "react";
import Lottie from "lottie-react";
import talkingAvatar from "./talking-avatar.json"; // Replace with your Lottie file
import "./App.css";

function App() {

  speechSynthesis.getVoices().forEach(voice => console.log( voice.name, voice.lang));

  const avatarRef = useRef();

  const handleTalk = () => {

    const msg = new SpeechSynthesisUtterance("Hello! How can I assist you today? What do you want to learn");
        // const msg = new SpeechSynthesisUtterance("namaste! aaj main aapkee kis prakar sahayata kar sakata hoon?");
    // const msg = new SpeechSynthesisUtterance("नमस्ते! आज मैं आपकी किस प्रकार सहायता कर सकता हूँ?");


    const voices = speechSynthesis.getVoices();

     // Set Samantha voice (or fallback if not found)
    const siriVoice = voices.find(voice => voice.name === "Ava");

    if (siriVoice) {
      msg.voice = siriVoice;
    }

    msg.onstart = () => {
      if (avatarRef.current) avatarRef.current.play();
    };

    msg.onend = () => {
      if (avatarRef.current) avatarRef.current.stop();
    };

    speechSynthesis.speak(msg);
  };

  return (
    <div className="app">
      <h2>Talking AI Avatar</h2>
      <Lottie
        lottieRef={avatarRef}
        animationData={talkingAvatar}
        loop
        autoplay={false}
        style={{ height: 300 }}
      />
      <button onClick={handleTalk}>Talk</button>
    </div>
  );
}

export default App;
