// src/App.js
import React, { useCallback, useEffect, useRef, useState } from "react";
import Lottie from "lottie-react";
import talkingAvatar from "./talking-avatar.json";
import { GoogleGenAI } from "@google/genai";

const MODEL = "gemini-live-2.5-flash-preview";
const OUTPUT_SAMPLE_RATE = 24000;

export default function App() {
  const avatarRef = useRef(null);

  const liveOpenRef = useRef(false);   // true while live socket is open
  const micGraphRef = useRef({ ctx: null, node: null, source: null }); // to cleanly tear down

  // ---- audio queue state (unchanged behavior) ----
  const audioQueueRef = useRef([]);
  const isModelSpeakingRef = useRef(false);

  const [session, setSession] = useState(null);
  const [audioCtx, setAudioCtx] = useState(null);
  const [micStream, setMicStream] = useState(null);
  const [isModelSpeaking, setIsModelSpeaking] = useState(false);

  // ---- NEW: chat history state ----
  const [history, setHistory] = useState([]); // {role: 'ai'|'user', text: string, ts: number}
  const historyEndRef = useRef(null);

  // Auto-scroll to newest message
  useEffect(() => {
    historyEndRef.current?.scrollIntoView({ behavior: "smooth" });
  }, [history]);

  // Lottie playback tied to speaking flag
  useEffect(() => {
    if (!avatarRef.current) return;
    if (isModelSpeaking) avatarRef.current.play();
    else avatarRef.current.stop();
  }, [isModelSpeaking]);

  // ---------- helpers: audio conversions ----------
  function floatTo16BitPCM(float32) {
    const out = new Int16Array(float32.length);
    for (let i = 0; i < float32.length; i++) {
      const s = Math.max(-1, Math.min(1, float32[i]));
      out[i] = s < 0 ? s * 0x8000 : s * 0x7fff;
    }
    return out;
  }

  function downsampleTo16k(float32, inRate) {
    const outRate = 16000;
    if (inRate === outRate) return float32;
    const ratio = inRate / outRate;
    const newLen = Math.floor(float32.length / ratio);
    const out = new Float32Array(newLen);
    let pos = 0;
    for (let i = 0; i < newLen; i++, pos += ratio) {
      out[i] = float32[Math.floor(pos)];
    }
    return out;
  }

  function arrayBufferToBase64(ab) {
    const bytes = new Uint8Array(ab);
    let bin = "";
    for (let i = 0; i < bytes.length; i++) bin += String.fromCharCode(bytes[i]);
    return btoa(bin);
  }

  function base64ToInt16(b64) {
    const bytes = Uint8Array.from(atob(b64), (c) => c.charCodeAt(0));
    return new Int16Array(bytes.buffer);
  }

  // ---------- NEW: history helpers ----------
  const pushHistory = (role, text) => {
    if (!text || !text.trim()) return;
    setHistory((h) => [...h, { role, text: text.trim(), ts: Date.now() }]);
  };

  // Try to robustly extract model text from live messages (covers multiple shapes)
  const extractModelText = (msg) => {
    if (!msg) return "";
    // Simple cases
    if (typeof msg.text === "string" && msg.text.trim()) return msg.text;

    // Candidate/parts shape
    const mt = msg.modelTurn || msg.output || msg.candidates || msg.response;
    const candidates = mt?.candidates || msg?.candidates;
    if (Array.isArray(candidates) && candidates.length) {
      const parts = candidates[0]?.content?.parts || candidates[0]?.content?.parts || [];
      const t = (parts || []).map((p) => p?.text).filter(Boolean).join(" ");
      if (t?.trim()) return t;
    }

    // Generic parts
    if (Array.isArray(msg.parts)) {
      const t = msg.parts.map((p) => p?.text).filter(Boolean).join(" ");
      if (t?.trim()) return t;
    }

    // Fallback: non-audio data field sometimes carries text
    if (msg?.data && typeof msg.data === "string" && !msg.speechUpdate) {
      return msg.data;
    }

    return "";
  };

  // ---------- audio queue processor ----------
  const processAudioQueue = useCallback(async () => {
    if (isModelSpeakingRef.current || audioQueueRef.current.length === 0) return;

    isModelSpeakingRef.current = true;
    setIsModelSpeaking(true);

    const audioChunk = audioQueueRef.current.shift();

    const ctx =
      audioCtx ||
      new (window.AudioContext || window.webkitAudioContext)({
        sampleRate: OUTPUT_SAMPLE_RATE,
      });
    if (!audioCtx) setAudioCtx(ctx);

    const f32 = new Float32Array(audioChunk.length);
    for (let i = 0; i < audioChunk.length; i++) f32[i] = audioChunk[i] / 0x8000;

    const buffer = ctx.createBuffer(1, f32.length, OUTPUT_SAMPLE_RATE);
    buffer.copyToChannel(f32, 0, 0);
    const src = ctx.createBufferSource();
    src.buffer = buffer;
    src.connect(ctx.destination);

    src.onended = () => {
      isModelSpeakingRef.current = false;
      if (audioQueueRef.current.length === 0) setIsModelSpeaking(false);
      else processAudioQueue();
    };

    src.start();
  }, [audioCtx]);

  // Load a tiny worklet that forwards mic float32 frames to main thread
async function setupMicWorklet(onChunk) {
  const ctx = new (window.AudioContext || window.webkitAudioContext)();
  const workletCode = `
    class MicForwarder extends AudioWorkletProcessor {
      process(inputs) {
        const ch = inputs[0][0];
        if (ch) {
          // copy so we don't send SAB-backed memory
          const out = new Float32Array(ch.length);
          out.set(ch);
          this.port.postMessage(out, [out.buffer]);
        }
        return true;
      }
    }
    registerProcessor('mic-forwarder', MicForwarder);
  `;
  const blob = new Blob([workletCode], { type: "application/javascript" });
  const url = URL.createObjectURL(blob);
  await ctx.audioWorklet.addModule(url);

  const stream = await navigator.mediaDevices.getUserMedia({ audio: true });
  const source = ctx.createMediaStreamSource(stream);
  const node = new AudioWorkletNode(ctx, 'mic-forwarder');
  node.port.onmessage = (e) => onChunk(e.data, ctx.sampleRate);

  source.connect(node);
  // don’t connect node to destination (we just tap the data)

  micGraphRef.current = { ctx, node, source, stream };
  return { ctx, node, source, stream };
}


  // ---------- Live session start / stop ----------
const startLive = async () => {
  // 1) fetch ephemeral token (same as before)
  const { token } = await fetch("http://localhost:8787/api/ephemeral-token").then(r => r.json());
  const ephemeralKey = typeof token === "string" ? token : token?.name;

  const ai = new GoogleGenAI({ apiKey: ephemeralKey, httpOptions: { apiVersion: "v1alpha" } });

  const instruction = `##PERSONA:
You are Neha Jain, a cheerful, friendly AI tutor created by AI Lab India. You live in Seattle and speak English fluently with a clear American accent. Your purpose is to help users learn Hindi in a welcoming and supportive manner. You should speak naturally, like a helpful human tutor. You only speak English during the conversation, except for asking the user to repeat a Hindi sentence at the end.

##INSTRUCTIONS:
