  const s = await ai.live.connect({
    model: "gemini-live-2.5-flash-preview",
    config: {
      responseModalities: ["AUDIO", "TEXT"],
      speechConfig: {
        languageCode: "hi-IN",
        voiceConfig: { prebuiltVoiceConfig: { voiceName: "Kore" } },
      },
      systemInstruction: instruction,
    },
    callbacks: {
  onopen: () => {
    liveOpenRef.current = true;
  },
  onmessage: (msg) => {
    // 1) capture any AI text to the bottom history
    const txt = extractModelText(msg);
    if (txt) pushHistory("ai", txt);

    // 2) capture audio chunks and play them
    // Gemini Live sends base64 PCM16 in `speechUpdate.audio`
    const b64 =
      msg?.speechUpdate?.audio ??
      (typeof msg?.data === "string" && !msg?.speechUpdate ? msg.data : null);

    if (b64) {
      const pcm16 = base64ToInt16(b64);      // you already have this helper
      audioQueueRef.current.push(pcm16);     // queue it
      processAudioQueue();                   // start/continue playback
    }
  },
  onerror: (e) => console.error("Live error:", e),
  onclose: () => {
    console.log("Live session closed");
    liveOpenRef.current = false;
    teardownMicGraph();                      // stop mic immediately on close
  },
}

  });
  setSession(s);

  // 2) Start mic via AudioWorklet and stream to Live, but only if the socket is open
  await setupMicWorklet((float32, rate) => {
    if (!liveOpenRef.current || !session) return;            // guard 1
    if (isModelSpeakingRef.current) return;                  // avoid echo during TTS
    try {
      const ds = downsampleTo16k(float32, rate);
      const pcm16 = floatTo16BitPCM(ds);
      session.sendRealtimeInput({
        audio: { data: arrayBufferToBase64(pcm16.buffer), mimeType: "audio/pcm;rate=16000" },
      });
    } catch (err) {
      // if the socket is closing/closed, ignore
      // prevents “WebSocket is already in CLOSING or CLOSED state”
    }
  });
};



  function teardownMicGraph() {
    const g = micGraphRef.current;
    if (!g) return;
    try { g.source && g.source.disconnect(); } catch {}
    try { g.node && g.node.port && (g.node.port.onmessage = null); } catch {}
    try { g.ctx && g.ctx.state !== "closed" && g.ctx.close(); } catch {}
    try { g.stream && g.stream.getTracks().forEach(t => t.stop()); } catch {}
    micGraphRef.current = { ctx: null, node: null, source: null, stream: null };
  }

  const stopLive = async () => {

      liveOpenRef.current = false;         // <— prevents any further sends
      teardownMicGraph();                  // <— kill mic graph immediately

    if (micStream) {
      micStream.getTracks().forEach((t) => t.stop());
      setMicStream(null);
    }
    if (session) {
      try {
        await session.close?.();
      } catch {}
      setSession(null);
    }
    if (audioCtx) {
      try {
        await audioCtx.close();
      } catch {}
      setAudioCtx(null);
    }
    // Keep history across sessions; clear only audio state
    audioQueueRef.current = [];
    isModelSpeakingRef.current = false;
    setIsModelSpeaking(false);
  };

  const sendText = async (text) => {
    if (!session || !text?.trim()) return;
    // Show user’s text in the history
    pushHistory("user", text);
    session.sendClientContent({
      text,
      turnComplete: true,
    });
  };

